useEffect(() => {
  axios.get(`http://127.0.0.1:8000/api_ott/movieview/${id}`)
    .then(response => {
      setMovie(response.data);
    })
    .catch(error => {
      console.error("There was an error fetching the movie data!", error);
    });
}, [id]);
