import React, { useEffect, useState } from 'react'
import Navbar2 from './Navbar2'
import './Style/style.css'
import Rating from 'react-rating';
import { FaStar } from 'react-icons/fa';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function Views() {
  var { id } = useParams();
  const [rating, setRating] = useState(0);
  const [movie, setMovies] = useState({  });

  useEffect(() => {
    axios.get(`http://127.0.0.1:8000/api_ott/viewapi/${id}`)
      .then(response => {
        setMovies(response.data);
      })
  }, [id]);

  return (
    <>
      <Navbar2 />
      <div className='background1'>
        <div className='left'>
          <h3 className='text-white'>{movies.title}</h3>
          <div>
            {movies.video ? (
              <video style={{ height: '70vh', marginLeft: '20%' }} controls>
                <source id="player" src={`http://127.0.0.1:8000/${movies.video}`} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
            ) : (
              <p>Video not available</p>
            )}
          </div>
          <p className='text-warning'>{movies.description}:</p>
          <div className="rating-container mt-3">
            <button className='btn btn-secondary mb-2' onClick={() => setRating(0)}>Clear</button>
            <Rating
              initialRating={rating}
              onChange={(rate) => setRating(rate)}
              emptySymbol={<FaStar color="#ccc" size={24} />}
              fullSymbol={<FaStar color="#ffc107" size={24} />}
            />
            <div>{rating}</div>
          </div>
        </div>
      </div>
    </>
  );
}
export default Views;
