import React, { useState } from 'react';
import './Style/Login.css'
import Navbar from './Navbar';
import {Link , useNavigate} from "react-router-dom";
import axios  from "axios"

function Signup () {
    var [name,setName] = useState("");
    var [email, setEmail] = useState("")
    var [password, setPassword] = useState("")
    var [passwordConf, setConfirm] = useState("")
    var [errorMessage, setErrorMessage] = useState("")
    var navigate = useNavigate();
    function registerUser(){
        var user = {
            name:name,
            email:email,
            password:password,
            password_confirmation:passwordConf
        }
        axios.post('http://127.0.0.1:8000/ottapi/signup',user).then(response=>{
            setErrorMessage("");
            navigate('/login');
        }).catch(error=>{})
    }
    return (
        <>
           <Navbar/>
        <div class=" background row ml-0">
            <div className="container col-5 mt-5">
                <div className="row mt-5 border bg-white rounded border-primary">
                    <div className="col-8 offset-2">
                        <h1 className='text-center text-primary'>Register</h1>
                        <div className="form-group">
                            <label className='text-secondary'>Name:</label>
                            <input
                                type="text"
                                className="form-control"
                            />
                        </div>
                        <div className="form-group">
                            <label className='text-secondary'>Email:</label>
                            <input
                                type="email"
                                className="form-control"
                            />
                        </div>
                        <div className="form-group">
                            <label className='text-secondary'>Password:</label>
                            <input
                                type="password"
                                className="form-control"
                            />
                        </div>
                        <div className="form-group">
                            <label className='text-secondary'>Confirm Password:</label>
                            <input type="password" className="form-control"/>
                        </div>
                        <div className="form-group">
                            <button className="btn btn-primary col-12" >
                                Submit
                            </button>
        
                        </div>
                    </div>
                </div>
            </div>
        </div>

        </>
    );
}

export default Signup;
