import React, { useEffect, useState } from "react";
import Navbar2 from "./Navbar2";
import { useParams } from "react-router-dom";
import axios from "axios";

function ViewMovie() {
    const { id } = useParams();
    const [movie, setMovie] = useState({ name: '', description: '', video: '', rating: '' });

    useEffect(() => {
        axios.get(`http://127.0.0.1:8000/api_ot/apiviewmovie/${id}`)
            .then(response => {
                setMovie(response.data);
            })
            .catch(error => {
                console.error("There was an error fetching the movie data!", error);
            });
    }, [id]);

    return (
        <div>
            <Navbar2 />
            <div>
                <h2 className="text-center mt-3 text-danger">{movie.name}</h2>
                <div>
                    {movie.video ? (
                        <video style={{ height: '70vh', marginLeft: '20%' }} controls>
                            <source id="player" src={`http://127.0.0.1:8000/${movie.video}`} type="video/mp4" />
                            Your browser does not support the video tag.
                        </video>
                    ) : (
                        <p>Video not available</p>
                    )}
                </div>
            </div>
            <div>
                <p style={{ marginLeft: '20%', marginRight: '20%' }} className="text-secondary"><b>{movie.description}</b></p>
                <h4 className="text-center mt-2">{movie.rating}</h4>
            </div>
        </div>
    );
}

export default ViewMovie;
